
import { assert } from '@codingame/monaco-vscode-api/vscode/vs/base/common/assert';
import { basename } from '@codingame/monaco-vscode-api/vscode/vs/base/common/path';

const PROMPT_FILE_EXTENSION = '.prompt.md';
const isPromptFile = (fileUri) => {
    return fileUri
        .path
        .endsWith(PROMPT_FILE_EXTENSION);
};
const getCleanPromptName = (fileUri) => {
    assert(isPromptFile(fileUri), `Provided path '${fileUri.fsPath}' is not a prompt file.`);
    return basename(fileUri.path, PROMPT_FILE_EXTENSION);
};

export { PROMPT_FILE_EXTENSION, getCleanPromptName, isPromptFile };
