

async function resizeImage(data) {
    if (typeof data === 'string') {
        data = convertStringToUInt8Array(data);
    }
    const blob = ( new Blob([data]));
    const img = ( new Image());
    const url = URL.createObjectURL(blob);
    img.src = url;
    return ( new Promise((resolve, reject) => {
        img.onload = () => {
            URL.revokeObjectURL(url);
            let { width, height } = img;
            if (width <= 768 || height <= 768) {
                resolve(data);
                return;
            }
            if (width > 2048 || height > 2048) {
                const scaleFactor = 2048 / Math.max(width, height);
                width = Math.round(width * scaleFactor);
                height = Math.round(height * scaleFactor);
            }
            const scaleFactor = 768 / Math.min(width, height);
            width = Math.round(width * scaleFactor);
            height = Math.round(height * scaleFactor);
            const canvas = document.createElement('canvas');
            canvas.width = width;
            canvas.height = height;
            const ctx = canvas.getContext('2d');
            if (ctx) {
                ctx.drawImage(img, 0, 0, width, height);
                canvas.toBlob((blob) => {
                    if (blob) {
                        const reader = ( new FileReader());
                        reader.onload = () => {
                            resolve(( new Uint8Array(reader.result)));
                        };
                        reader.onerror = (error) => reject(error);
                        reader.readAsArrayBuffer(blob);
                    }
                    else {
                        reject(( new Error('Failed to create blob from canvas')));
                    }
                }, 'image/png');
            }
            else {
                reject(( new Error('Failed to get canvas context')));
            }
        };
        img.onerror = (error) => {
            URL.revokeObjectURL(url);
            reject(error);
        };
    }));
}
function convertStringToUInt8Array(data) {
    const base64Data = data.includes(',') ? data.split(',')[1] : data;
    if (isValidBase64(base64Data)) {
        return Uint8Array.from(atob(base64Data), char => char.charCodeAt(0));
    }
    return ( new TextEncoder()).encode(data);
}
function convertUint8ArrayToString(data) {
    try {
        const decoder = ( new TextDecoder());
        const decodedString = decoder.decode(data);
        return decodedString;
    }
    catch {
        return '';
    }
}
function isValidBase64(str) {
    return /^[A-Za-z0-9+/]*={0,2}$/.test(str) && (() => {
        try {
            atob(str);
            return true;
        }
        catch {
            return false;
        }
    })();
}

export { convertStringToUInt8Array, convertUint8ArrayToString, resizeImage };
